<script>
    import { MetaUtils } from "./utils/meta";

    export default {
        name: 'App',
        data: function () {
            return {
                email: '',
                emailForSubscription: '',
                token: '',
                isContinueButtonDisabled: true,
                isCaptchaShown: true,
                isEmailError: false,
                isSuccess: false,
                isAdaptedMenuShown: false,
                captchaKey: '',
            };
        },
        methods: {
            recaptchaOnSuccess: function () {
                this.isContinueButtonDisabled = false;
            },
            reload: function () {
                location.reload();
            },
            hideCaptcha: function () {
                this.isCaptchaShown = false;
            },
            onSubscribeClick: async function () {
                this.emailForSubscription = this.emailForSubscription.trim();

                if (!this.validateEmail(this.emailForSubscription)) {
                    this.isEmailError = true;
                    this.isSuccess = false;

                    return;
                }

                const request = {
                    method: 'POST',
                };

                let response = await fetch(`/${this.emailForSubscription}`, request);

                if (!response.ok) {
                    return;
                }

                this.isEmailError = false;
                this.isSuccess = true;
            },
            onContinueClick: async function () {
                this.email = this.email.trim();

                if (!this.validateEmail(this.email)) {
                    return;
                }

                const request = {
                    method: 'PUT',
                };

                let response = await fetch(`/${this.email}`, request);

                if (!response.ok) {
                    return;
                }

                this.token = await response.json();
            },
            toggleAdaptedMenu: function() {
                this.isAdaptedMenuShown = !this.isAdaptedMenuShown;
            },
            validateEmail: function(email) {
                const rgx = /.*@.*\..*$/;

                return rgx.test(email);
            },
            setMetaContext: function () {
                this.captchaKey = MetaUtils.getMetaContent('captcha-site-key');
            }
        },
        mounted() {
            this.setMetaContext();
        }
    }
</script>

